import { Box, IconButton } from '@material-ui/core';
import React, { useEffect, useState, useContext } from 'react'
import Icon from '../img/Icon.png'
import { useHistory } from 'react-router';
import { BackgroundLogin, ButtonEnter, ImageLogin, InputLogin, TextButton } from '../theme/Tema';


function LoginPage(props) {
    const [valueInput, setValueInput] = useState()
  
const history = useHistory()
   

const submitProps = (value) => {
   
if(value){
    alert("digite um valor válido")
}else{
    props.info.setConfirm(value)
}
    
}

const handleInput = (e) => {
setValueInput(e.target.value)
}

    return (
        <BackgroundLogin>
            <Box display = 'flex' justifyContent = 'center' flexDirection = 'column'
             alignItems = 'center' paddingTop = '250px'>
                <Box width = '50px' margin = 'auto' marginRight = '200px'>
                    <ImageLogin src ={Icon}>

                    </ImageLogin>
                    </Box>

                        <Box>
                        <InputLogin
                        placeholder = 'Usuário'
                        required
                        value = {valueInput}
                        onChange = {handleInput}
                        ></InputLogin>
                            </Box>

                            <Box>
                        <ButtonEnter onClick = {() => submitProps(valueInput)}>
                    <TextButton>Entrar</TextButton>
                    <IconButton src = 'https://cdn4.iconfinder.com/data/icons/feather/24/arrow-right-512.png'></IconButton>
                        </ButtonEnter>
                        </Box>
                    <div>
                </div>
            </Box>
        </BackgroundLogin>
    );
}

export default LoginPage;
