import React, { useContext, useEffect, useState } from 'react'
import styled from 'styled-components'
import { Box } from '@material-ui/core';
import axios from 'axios';
import { useHistory, useParams } from 'react-router-dom';
import reposIcon from '../img/reposIcon.png'
import followers from '../img/followers.png'
import following from '../img/following.png'

const ImgAvatar = styled.img`
height: 250px;
width: 270px;
border-radius: 160px;
justify-content: space-evenly;
-webkit-transition: all 1s ease; 
:hover{
  -webkit-transform:scale(1.25); 
}
`
const IconDescription = styled.img`
height: 65px;
width: 65px;
padding-right: 24px;
`
const IconFollowing = styled.img`
height: 120px;
width: 125px;

`
const BoxIcons = styled.div`
display: flex;
align-items: center;
`
const BoxIconRepo = styled.div `
display: flex;
align-items: center;
cursor: pointer;
-webkit-transition: all 1s ease; 

:hover{
  -webkit-transform:scale(1.25); 
}
`
export const IconBackInfo = styled.img`
height: 65px;
margin-top: -32px;
animation: go-back 1s infinite alternate;
@keyframes go-back {
  from {
    transform: translateX(0);
  }
  to {
    transform: translateX(50px);
  }
}
`
const IconStarred = styled.img`
height: 65px;
margin-top:-32px;
`



function AllInfoUser() {

  const [userInfo, setUserInfo] = useState()

  const params = useParams().username
  const history = useHistory()

  useEffect(() => {
    autenticacao()
  }, [])

  const autenticacao = () => {
    axios.get(`https://api.github.com/users/${params}`)
      .then((res) => {

        setUserInfo(res.data)
        console.log(res.data)
      }).catch((err) => {
        alert('Usuário não encontrado')
        history.goBack()
      })
  }
/*

*/
const goRepository = () => {
  if(userInfo.public_repos != 0 ){
    history.push(`/users/${params}/repos`)
  }else{ 
    alert("Não há repositórios a serem mostrados")
  }
}


  return (
    <Box>

      <Box bgcolor='#1F1F1F' height='400px' color='white' display='flex' justifyContent='space-evenly' alignItems='center'>
        <Box>
          <ImgAvatar src={userInfo && userInfo.avatar_url}></ImgAvatar>

        </Box>
        <Box>
          <p className='p'>#{userInfo && userInfo.login}</p>
        </Box>
      </Box>
      <Box display = 'flex' justif>
        <IconBackInfo src = 'https://cdn4.iconfinder.com/data/icons/ionicons/512/icon-ios7-arrow-left-512.png'>
        </IconBackInfo>
      <IconStarred src = 'https://cdn4.iconfinder.com/data/icons/ionicons/512/icon-ios7-arrow-right-128.png'>

      </IconStarred>
      </Box>
      <Box display='flex' justifyContent='space-evenly' alignItems='center' paddingTop='200px'>
        <BoxIcons>
            <IconDescription
              src={followers}>
            </IconDescription>
          <p className = 'iconP'>
         
            {userInfo && userInfo.followers} Followers
          </p>
        </BoxIcons>
        <BoxIcons>
          <Box>
             <IconFollowing
              src={following}>
            </IconFollowing>
          </Box>
          
          <p className = 'iconP'>
           
            {userInfo && userInfo.following} Following(s)
          </p>
        </BoxIcons>
       
       
        <BoxIconRepo onClick = {goRepository}>
        <Box>
          <IconDescription
            src={reposIcon}>
          </IconDescription>
        </Box>
         
          
          <p className = 'iconP' >
            {userInfo && userInfo.public_repos} Public repositories
          </p>
        </BoxIconRepo>






      </Box>
    </Box>

  );
}

export default AllInfoUser;
