import React, {useContext, useEffect, useState} from 'react'
import InfosContext from '../context/InfosContext'
import { BackgroundLogin } from '../theme/Tema';
import styled from 'styled-components'
import { Box } from '@material-ui/core';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ImgAvatar = styled.img `
height: 250px;
width: 270px;
border-radius: 160px;
justify-content: space-evenly;
-webkit-transition: all 1s ease; 
:hover{
  -webkit-transform:scale(1.25); 
}
`
const IconDescription = styled.img`
height: 65px;
`

function AllInfoUser() {

  const [userInfo, setUserInfo] = useState()

const params = useParams().username

useEffect(()=>{
autenticacao()
test()
},[])
  
const autenticacao = () => {
  axios.get(`https://api.github.com/users/${params}`)
  .then((res)=>{
  
    setUserInfo(res.data) 
    console.log(res.data)
  }).catch((err)=>{
      alert(err)
  
  })
  }

  const test = () => {
    axios.get(`https://api.github.com/users/${params}/repos`)
    .then((res)=>{
     
      console.log(res.data)
    }).catch((err)=>{
        alert(err)
    
    })
    }


    return (
      <Box>
          <Box bgcolor = '#1F1F1F' height = '400px' color ='white'  display = 'flex' justifyContent = 'space-evenly' alignItems ='center'>
        <Box>
          <ImgAvatar  src = {userInfo&&userInfo.avatar_url}></ImgAvatar>
          
        </Box>
        <Box>
          <p className = 'p'>#{userInfo&&userInfo.login}</p>
        </Box>
      </Box>
      <Box display = 'flex' justifyContent = 'space-evenly' alignItems ='center' paddingTop = '200px'>
        <p><IconDescription src = 'https://cdn3.iconfinder.com/data/icons/knowledge-management-glyph-2/64/repository-storage-store-database-data-file-512.png'>
        </IconDescription>
        {userInfo&&userInfo.followers} Followers
        </p>

        <p>{userInfo&&userInfo.following} Following(s)</p>
        <p>{userInfo&&userInfo.public_repos} Public repositories</p>
      </Box>
      </Box>
    
    );
  }
  
  export default AllInfoUser;
