import { Box, IconButton } from '@material-ui/core';
import React, { useEffect, useState, useContext } from 'react'
import Icon from '../img/Icon.png'
import { useHistory } from 'react-router';
import { BackgroundLogin, ButtonEnter, ImageLogin, InputLogin, TextButton } from '../theme/Tema';
import axios from 'axios'
import '../master.css'
import InfosContext from '../context/InfosContext'

function LoginPage() {
    const [valueInput, setValueInput] = useState()
    const [userInfo, setUserInfo] = useState()
    
const history = useHistory()


const handleInput = (e) => {
setValueInput(e.target.value)
if(e.key === "Enter"){
    autenticacao(valueInput)
}
}

const autenticacao = (meth) => {
    axios.get(`https://api.github.com/users/${meth}`)
    .then((res)=>{
      console.log(res.data)
      setUserInfo(res.data)  
     goToHomePage()
    }).catch((err)=>{
        alert(err)
    
    })
    }

const goToHomePage = () => {
    if(userInfo&&userInfo === undefined){
        alert('Error: Username invalid')
    }else{
        history.push('/home')
    }

}


    return (
        <BackgroundLogin>
            <Box display = 'flex' justifyContent = 'center' flexDirection = 'column'
             alignItems = 'center' paddingTop = '250px'>
                <Box width = '50px' margin = 'auto' marginRight = '980px' marginBottom = '42px'>
                    <ImageLogin src ={Icon}>

                    </ImageLogin>
                    </Box>

                        <Box>
                        <input className = 'inputLogin'
                        placeholder = 'UsuÃ¡rio'
                        required
                        value = {valueInput}
                        onChange = {handleInput}
                        onKeyUp = {handleInput}
                        ></input>
                            </Box>

                            <Box>
                        <ButtonEnter onClick = {() =>autenticacao(valueInput)}>
                    <TextButton>Entrar</TextButton>
                    <IconButton src = 'https://cdn4.iconfinder.com/data/icons/feather/24/arrow-right-512.png'></IconButton>
                        </ButtonEnter>
                        </Box>
                    <div>
                </div>
            </Box>
        </BackgroundLogin>
    );
}

export default LoginPage;
