import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { useHistory, useParams } from 'react-router-dom'
import {Box} from '@material-ui/core';
import '../master.css'
import styled from 'styled-components';
import '../master.css'
import { TextButton, IconButton,ButtonEnter } from '../theme/Tema';

const BackgroundRepos = styled.div`
background-color: #292929;
height:96.4vh;

`
const BoxGrid = styled.div `
 display: grid; 
  grid-template-columns: 1fr 1fr 1fr 1fr; 
  grid-template-rows: 1fr 1fr 1fr; 
  gap: 0px; 
grid-gap: 10px;
padding: 8px;
`

const NamePage = styled.h1`
font-size: 50px;
`
const BoxInfoRepo = styled.div`
background-color: darkgrey;
padding: 15px;
height: 150px;
display: flex;
justify-content: center;
align-items: center;

`


function Repos_url() {
const params = useParams().username
const [infoRepo, setInfoRepo] = useState([]) 
const history = useHistory()
const [starred, setStarred] = useState([])
useEffect(()=>{
  user_repo()
validStarred()
},[])

  const user_repo = () => {
    axios.get(`https://api.github.com/users/${params}/repos`)
      .then((res) => {
        setInfoRepo(res.data)
        
      }).catch((err) => {
        alert(err)

      })
  }

  const map_repositorios = infoRepo.map((repo)=>{
     return(
         <BoxInfoRepo key = {repo.id}>     
        <p id = 'nameRepo'>{repo.name}</p>

      </BoxInfoRepo> 
   
     ) 
  })
console.log(params)
  const validStarred = () => {
    axios.get(`https://api.github.com/users/${params}/starred`)
      .then((res) => {
        console.log(res.data)
       setStarred(res.data)
      }).catch((err) => {
        alert(err.messege)

      })
  }

const goStarred = () => {
  if(starred)
history.push(`/users/${params}/starred`)
}

  return (
    <BackgroundRepos>
        <Box bgcolor = 'white' height = '100px'>
      <NamePage className = 'iconP'>Reposit√≥rios</NamePage>
      </Box>
      <BoxGrid display= 'flex'>
        {map_repositorios}
      </BoxGrid>
      <Box display = 'flex' justifyContent = 'center'>
        <ButtonEnter >
      <TextButton>Starred</TextButton>
      </ButtonEnter>
      </Box>
      
    </BackgroundRepos>
    
    );
  }
  
  export default Repos_url;
