import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom'
import { BackgroundLogin } from '../theme/Tema'
import { Box, IconButton } from '@material-ui/core';
import '../master.css'
import styled from 'styled-components';

const BackgroundRepos = styled.div`
background-color: #292929;
height:96.5vh;
`


const NamePage = styled.h1`
font-size: 50px;
`
const BoxInfoRepo = styled.div`
background-color: red;

`


function Repos_url() {
const params = useParams().username
const [infoRepo, setInfoRepo] = useState([]) 


useEffect(()=>{
test()
},[])

  const test = () => {
    axios.get(`https://api.github.com/users/${params}/repos`)
      .then((res) => {
        setInfoRepo(res.data)
        
      }).catch((err) => {
        alert(err)

      })
  }
  console.log(infoRepo)
  const map_repositorios = infoRepo.map((repo)=>{
     return(
       <BoxInfoRepo>
      <p>{repo.id}</p>
      </BoxInfoRepo>
     ) 
  })



  return (
    <BackgroundRepos>
        <Box bgcolor = 'white' height = '100px'>
      <NamePage className = 'iconP'>Reposit√≥rios</NamePage>
      </Box>
      {map_repositorios}
    </BackgroundRepos>
    
    );
  }
  
  export default Repos_url;
