import React, { useEffect, useState } from "react";
import { Route, Switch, BrowserRouter, useHistory } from "react-router-dom";
import AllInfoUser from "../home/AllInfoUser";
import LoginPage from "../login/LoginPage";
import Followers from "../pages/Followers";
import Following from "../pages/Following";
import Repos_url from "../pages/Repos_url";
import axios from 'axios'
import InfosContext from '../context/InfosContext'

function Router() {

const [confirm, setConfirm] = useState()


const confirmUser = {
  confirm,
  setConfirm
}




useEffect(()=>{
  if(confirm != undefined){
    aut()
   
  }
})

const history = useHistory()

console.log(history)
const aut = () => {
  axios.get(`https://api.github.com/users/${confirm}`)
  .then((res)=>{
  if(res.data.login === "undefined" || !res.data.login){
     alert("Digite um usu치rio v치lido")
  }else{
    
}
  }).catch((err)=>{
      alert(err)
  
  })
  }

  return (
    <InfosContext.Provider value = {confirmUser}>
          <BrowserRouter>
      <Switch>
        <Route exact path="/">
          <LoginPage info = {confirmUser} />
        </Route>

        <Route exact path="/home">
          <AllInfoUser  info = {confirmUser} />
        </Route>

        <Route exact path="/repositorios">
          <Repos_url  info = {confirmUser} />
        </Route>

        <Route exact path="/following">
          <Following  info = {confirmUser} />
          
        </Route>

        <Route exact path="/followers">
          <Followers  info = {confirmUser} />
        </Route>
      </Switch>
    </BrowserRouter>
    </InfosContext.Provider>

  );
}

export default Router;
